import java.io.*; import java.util.*; public class crossroad { public static void main(String[] args) throws IOException { Scanner in = new Scanner(new File("crossroad.in")); int n = in.nextInt(); int[] arr = new int[11]; int result=0; for(int i = 0; i < n; i++) { int a = in.nextInt(); int b = in.nextInt()+1; if(arr[a] > 0 && arr[a] != b) result++; arr[a] = b;} in.close(); System.out.println(result); PrintWriter out = new PrintWriter(new File("crossroad.out")); out.println(result); out.close(); } }
